verEffect(espButton) addHoverEffect(customizeButton) addHoverEffect(closeButton) addHoverEffect(fillToggleButton) addHoverEffect(rainbowToggleButton) for _,button in ipairs(outlineColorButtons) do addHoverEffect(button) end for _,button in ipairs(fillColorButtons) do addHoverEffect(button) end local function getRainbowColor(t) local r=math.sin(t*2*math.pi)*0.5+0.5 local g=math.sin(t*2*math.pi+2*math.pi/3)*0.5+0.5 local b=math.sin(t*2*math.pi+4*math.pi/3)*0.5+0.5 return Color3.new(r,g,b) end local pulseTime=0 runService.Heartbeat:Connect(function(deltaTime) if espEnabled and (pulseEnabled or rainbowEnabled) then pulseTime=pulseTime+deltaTime*pulseSpeed local pulseValue=(math.sin(pulseTime)+1)/2 local currentOutlineTrans=pulseEnabled and (pulseMinTrans+(pulseMaxTrans-pulseMinTrans)*pulseValue) or outlineTransparency local currentOutlineColor=rainbowEnabled and getRainbowColor(pulseTime) or outlineColor local currentFillColor=rainbowEnabled and getRainbowColor(pulseTime) or fillColor for _,data in pairs(espObjects) do local highlight=data[1] highlight.OutlineTransparency=currentOutlineTrans highlight.OutlineColor=currentOutlineColor highlight.FillColor=currentFillColor end end end) local function applyESP(character) if character and character:FindFirstChild("HumanoidRootPart") then local highlight=Instance.new("Highlight") highlight.Parent=character highlight.FillTransparency=fillEnabled and fillTransparency or 1 highlight.FillColor=fillColor highlight.OutlineTransparency=outlineTransparency highlight.OutlineColor=outlineColor local billboard=Instance.new("BillboardGui") billboard.Parent=character billboard.Adornee=character:FindFirstChild("Head") billboard.Size=UDim2.new(0,200,0,50) billboard.StudsOffset=Vector3.new(0,2,0) billboard.AlwaysOnTop=true local textLabel=Instance.new("TextLabel") textLabel.Parent=billboard textLabel.Size=UDim2.new(1,0,1,0) textLabel.BackgroundTransparency=1 textLabel.TextColor3=Color3.fromRGB(255,255,255) textLabel.Font=Enum.Font.GothamBold textLabel.TextSize=14 textLabel.TextStrokeTransparency=0.5 local plr=game.Players:GetPlayerFromCharacter(character) if plr then local teamName=plr.Team and plr.Team.Name or "No Team" local humanoid=character:FindFirstChild("Humanoid") local health=humanoid and math.floor(humanoid.Health) or 0 local maxHealth=humanoid and math.floor(humanoid.MaxHealth) or 100 local distance=player.Character and player.Character:FindFirstChild("HumanoidRootPart") and (character.HumanoidRootPart.Position-player.Character.HumanoidRootPart.Position).Magnitude or 0 textLabel.Text=string.format("%s | %.1fm | %d/%d HP | %s",teamName,distance,health,maxHealth,plr.Name) end espObjects[character]={highlight,billboard,textLabel} end end local function updateESP() if not player.Character or not player.Character:FindFirstChild("HumanoidRootPart") then return end for _,plr in pairs(game.Players:GetPlayers()) do if plr~=player and plr.Character and plr.Character:FindFirstChild("HumanoidRootPart") then local char=plr.Character local rootPart=char:FindFirstChild("HumanoidRootPart") local humanoid=char:FindFirstChild("Humanoid") local espData=espObjects[char] if rootPart and humanoid and espData then local distance=(rootPart.Position-player.Character.HumanoidRootPart.Position).Magnitude local teamName=plr.Team and plr.Team.Name or "No Team" local health=math.floor(humanoid.Health) local maxHealth=math.floor(humanoid.MaxHealth) espData[3].Text=string.format("%s | %.1fm | %d/%d HP | %s",teamName,distance,health,maxHealth,plr.Name) if distance<=maxDistance then espData[1].Enabled=true espData[2].Enabled=true local tweenInfo=TweenInfo.new(0.5,Enum.EasingStyle.Sine,Enum.EasingDirection.InOut) local tween=tweenService:Create(espData[2],tweenInfo,{StudsOffset=Vector3.new(0,2+0.5*math.sin(tick()),0)}) tween:Play() else espData[1].Enabled=false local tweenInfo=TweenInfo.new(0.5,Enum.EasingStyle.Sine,Enum.EasingDirection.InOut) local tween=tweenService:Create(espData[2],tweenInfo,{StudsOffset=Vector3.new(0,2,0),ExtentsOffset=Vector3.new(0,0,0)}) tween:Play() espData[2].Enabled=false end end end end end local function refreshESP() for _,data in pairs(espObjects) do for _,obj in pairs(data) do obj:Destroy() end end espObjects={} for _,plr in pairs(game.Players:GetPlayers()) do if plr~=player and plr.Character then applyESP(plr.Character) end end end local function updateChams() for _,data in pairs(espObjects) do local highlight=data[1] highlight.OutlineColor=outlineColor highlight.OutlineTransparency=outlineTransparency highlight.FillColor=fillColor highlight.FillTransparency=fillEnabled and fillTransparency or 1 end end local function toggleESP() espEnabled=not espEnabled espButton.Text=espEnabled and "Disable ESP" or "Enable ESP" local tweenInfo=TweenInfo.new(0.2,Enum.EasingStyle.Sine,Enum.EasingDirection.Out) local tween=tweenService:Create(espButton,tweenInfo,{BackgroundColor3=espEnabled and Color3.fromRGB(50,200,50) or Color3.fromRGB(45,45,55)}) tween:Play() tween.Completed:Connect(function() if espEnabled then local resetTween=tweenService:Create(espButton,tweenInfo,{BackgroundColor3=Color3.fromRGB(45,45,55)}) resetTween:Play() end end) if espEnabled then refreshESP() espConnection=runService.Heartbeat:Connect(function() if espEnabled then updateESP() end end) for _,plr in pairs(game.Players:GetPlayers()) do if plr~=player then plr.CharacterAdded:Connect(function(character) if espEnabled then applyESP(character) end end) end end else if espConnection then espConnection:Disconnect() end for _,data in pairs(espObjects) do for _,obj in pairs(data) do obj:Destroy() end end espObjects={} end end espButton.MouseButton1Click:Connect(toggleESP) customizeButton.MouseButton1Click:Connect(function() animateMenu(false,frame) animateMenu(true,customizeFrame) end) closeButton.MouseButton1Click:Connect(function() animateMenu(false,frame) animateMenu(false,customizeFrame) if espConnection then espConnection:Disconnect() end end) userInputService.InputBegan:Connect(function(input,gam